import React, { useState, useEffect } from 'react';

function BookApp() {
  // State variables
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '', year: '', isbn: '' });

  // Fetch all books on component mount
  useEffect(() => {
    fetchBooks();
  }, []);

  // Function to fetch all books
  const fetchBooks = () => {
    fetch('http://localhost:8080/api/books') // Replace with your API endpoint
      .then(response => response.json())
      .then(data => setBooks(data))
      .catch(error => console.error('Error fetching books:', error));
  };

  // Function to handle input change
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewBook({ ...newBook, [name]: value });
  };

  // Function to handle form submit for adding a new book
  const handleAddBook = (event) => {
    event.preventDefault();
    fetch('http://localhost:3000/books', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newBook)
    })
    .then(() => {
      setNewBook({ title: '', author: '', year: '', isbn: '' });
      fetchBooks(); // Refresh book list
    })
    .catch(error => console.error('Error adding book:', error));
  };

  // Function to handle delete book
  const handleDeleteBook = (id) => {
    fetch(`http://localhost:3000/books/${id}`, {
      method: 'DELETE'
    })
    .then(() => fetchBooks()) // Refresh book list
    .catch(error => console.error('Error deleting book:', error));
  };

  return (
    <div>
      <h1>Book CRUD Application</h1>
      {/* Form to add new book */}
      <form onSubmit={handleAddBook}>
        <input type="text" name="title" value={newBook.title} onChange={handleInputChange} placeholder="Title" required />
        <input type="text" name="author" value={newBook.author} onChange={handleInputChange} placeholder="Author" required />
        <input type="text" name="year" value={newBook.year} onChange={handleInputChange} placeholder="Year" required />
        <input type="text" name="isbn" value={newBook.isbn} onChange={handleInputChange} placeholder="ISBN" required />
        <button type="submit">Add Book</button>
      </form>
      {/* Book list */}
      <ul>
        {books.map(book => (
          <li key={book.id}>
            <div>{book.title}</div>
            <div>{book.author}</div>
            <div>{book.year}</div>
            <div>{book.isbn}</div>
            <button onClick={() => handleDeleteBook(book.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BookApp;
